export default {
  name: 'Loading',
  data () {
    return {
      seeLoading: false,
    }
  },
  methods: {
    readyListeners () {
      this.$proOn('setLoading', (val) => {
        this.seeLoading = val
      })
    },
  },
  mounted () {
    this.readyListeners()
  },
}
